import React, { useState, useEffect } from "react";
import Navbare from "./components/Navbare/navbar";
import Prestations from './components/Prestations/prestation';

export default function Services (){

  useEffect(() => {
    if (window.innerWidth >= 1024) {
      // Si la largeur de la fenêtre est supérieure ou égale à 1024 (format ordinateur)
      // Éviter que le défilement ne se situe au-dessus de 1% de la hauteur de la page
      const windowHeight = window.innerHeight;
      const maxScroll = windowHeight * 0.01;
      
      const handleScroll = () => {
        if (window.scrollY < maxScroll) {
          window.scrollTo(0, maxScroll);
        }
      };
      
      window.addEventListener("scroll", handleScroll);

      // Nettoyer l'écouteur d'événement lorsque le composant est démonté
      return () => {
        window.removeEventListener("scroll", handleScroll);
      };
    }
  }, []);

  return(
  <div>
    <div><Navbare/></div>
    <div className=" sm:mt-40"><Prestations/></div>
    <div>
      <hr className="border-t-2 mt-16 sm:ml-96 sm:mr-96 mr-4 border-neutral-500 my-2" />
      <h1 className="mt-16 text-center text-neutral-800 text-2xl font-signikaSemiBold">Comment sont calculer nos tarifs ?</h1>
    </div>
  </div>
  );
}
