import React, { useState } from 'react';
import './navbar.css'; // Assurez-vous d'importer le fichier CSS contenant les styles
import { Link } from 'react-router-dom';
import Hamburger from 'hamburger-react';

function Navbar() {
    const [isOpen, setOpen] = useState(false);

    const menuStyles = {
        maxHeight: isOpen ? '200px' : '40px',
        overflow: 'hidden',
        transition: 'max-height 0.3s ease-in-out',
    };

    const burgerStyles = {
        position: 'absolute',
        top: '10px',
        left: '20px',
    };

    return (
        <nav className='navbar1'> {/* Utilisation de la classe 'navbar1' */}
            <div className='container header1'>
                <div className='md:hidden lg:hidden xl:hidden' style={burgerStyles}>
                    <Hamburger color="#EEB42C" easing="ease-in" toggled={isOpen} toggle={setOpen} />
                </div>
                <ul className='menu1'> {/* Utilisation de la classe 'menu1' */}
                    <li><Link to="/" className='text-white'>Home</Link></li>
                    <li><Link to="/tools" className='text-white'>Mon matériel</Link></li>
                    <li><Link to="/services" className='text-white'>Services</Link></li>
                    <li><Link to="/about" className='text-white'>A propos</Link></li>
                    <li><Link to="/contact" className='text-white'>Contact</Link></li>
                </ul>
            </div>
            <div className=''>
                <div className='md:hidden lg:hidden xl:hidden' style={menuStyles}>
                    <div className='text-3xl font-skyvision text-white block py-2 text-center'><h1>SkyVision</h1></div>
                    <Link to="/" className='text-white block py-2 text-center'>Home</Link>
                    <Link to="/tools" className='block py-2 text-white text-center'>Mon matériel</Link>
                    <Link to="/services" className='text-white block py-2 text-center'>Services</Link>
                    <Link to="/about" className='text-white block py-2 text-center'>A propos</Link>
                    <Link to="/contact" className='text-white block py-2 text-center'>Contact</Link>
                </div>
            </div>
        </nav>
    );
}

export default Navbar;
